def result(request):
    q1 = request.session.get('question1')
    q2 = request.session.get('question2')
    q3 = request.session.get('question3')
    q4 = request.session.get('question4')

    # 답변 조합에 따른 결과 결정
    result_key = (q1, q2, q3, q4)
    result_message, result_text, image_path = RESULT_MAPPING.get(result_key, ('결과X','결과를 찾을 수 없습니다.', 'psychotest/images/default_flower.png'))

    return render(request, 'result.html', {'result_message': result_message, 'result_text': result_text, 'image_path': image_path})
